  const domainStats = {};
  papers.forEach(p => {
    if (!domainStats[p.domain]) domainStats[p.domain] = [];
    domainStats[p.domain].push(p);
  });
  
  document.addEventListener("DOMContentLoaded", () => {
    const container = document.getElementById("dashboard");
    if (!container) {
      console.error(" Missing element with ID 'dashboard' in HTML.");
      return;
    }
  
    // Title header
    const header = document.createElement("h1");
    header.textContent = "NASA Bioscience Research Dashboard";
    header.style.color = "#58a6ff";
    header.style.textAlign = "center";
    header.style.marginTop = "20px";
    container.appendChild(header);
  
    // Domain cards
    Object.entries(domainStats).forEach(([domain, papers]) => {
      const section = document.createElement("div");
      section.className = "domain-section";
  
      const title = document.createElement("h2");
      title.textContent = `${domain.toUpperCase()} (${papers.length})`;
      section.appendChild(title);
  
      // Paper list
      const list = document.createElement("ul");
      papers.forEach(paper => {
        const item = document.createElement("li");
        const link = document.createElement("a");
        link.href = paper.link;
        link.target = "_blank";
        link.textContent = paper.title;
        item.appendChild(link);
        list.appendChild(item);
      });
      section.appendChild(list);
      container.appendChild(section);
    });
  });
  
  const style = document.createElement("style");
  style.textContent = `
    body {
      background-color: #0a0a0a;
      color: #9ad0ff;
      font-family: 'Segoe UI', sans-serif;
      margin-left: 100px;
    }
  
    h1 {
      color: #58a6ff;
      font-size: 2em;
    }
  
    h2 {
      color: #58a6ff;
      margin-top: 25px;
      border-bottom: 1px solid #1f6feb;
      padding-bottom: 5px;
    }
  
    ul {
      list-style-type: none;
      padding-left: 0;
    }
  
    li {
      margin: 5px 0;
    }
  
    a {
      color: #9ad0ff;
      text-decoration: none;
    }
  
    a:hover {
      color: #58a6ff;
      text-decoration: underline;
    }
  
    .domain-section {
      margin-bottom: 30px;
    }
  `;
  document.head.appendChild(style);
  